import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [covidData, setCovidData] = useState(null);
  const [error, setError] = useState(null);

  // Handle file upload
  const onFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  // Handle file upload submission
  const onFileUpload = () => {
    const formData = new FormData();
    formData.append("file", selectedFile);

    axios.post("/upload", formData)
      .then(response => {
        setCovidData(response.data);  // Assuming covid data is returned
        setError(null);
      })
      .catch(err => {
        setError("Error uploading file: " + err.message);
      });
  };

  // Handle download of filtered CSV data
  const onDownloadFiltered = () => {
    axios.get("/downloadFiltered", { responseType: 'blob' })
      .then(response => {
        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'Updated_Main.csv');  // Filename to download
        document.body.appendChild(link);
        link.click();
      });
  };

  // Handle download of adverse effects CSV data
  const onDownloadAdverse = () => {
    axios.get("/downloadAdverse", { responseType: 'blob' })
      .then(response => {
        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'AdverseEffect1.csv');
        document.body.appendChild(link);
        link.click();
      });
  };

  return (
    <div className="App">
      <h2>Upload Medical Prescriptions</h2>
      <input type="file" onChange={onFileChange} />
      <button onClick={onFileUpload}>
        Upload
      </button>

      {/* Show COVID data after upload */}
      {covidData && (
        <div>
          <h3>COVID Data</h3>
          <pre>{JSON.stringify(covidData, null, 2)}</pre>
        </div>
      )}

      {/* Show any error messages */}
      {error && (
        <div style={{ color: "red" }}>
          <p>{error}</p>
        </div>
      )}

      <h3>Download Processed Data</h3>
      <button onClick={onDownloadFiltered}>Download Filtered Data (CSV)</button>
      <button onClick={onDownloadAdverse}>Download Adverse Effects (CSV)</button>
    </div>
  );
}

export default App;
